import React, { useState } from 'react';

const Demo = () => {
  const [selectedOption, setSelectedOption] = useState('');

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleButtonClick = () => {
    alert('Selected Option: ' + selectedOption);
  };

  return (
    <div>
      <select value={selectedOption} onChange={handleOptionChange}>
        <option>Select Letter</option>
        <option value="relieving" onMouseOver={handleButtonClick}>Relieving</option>
        <option value="experience" onMouseOver={handleButtonClick}>Experience</option>
        <option value="offer" onMouseOver={handleButtonClick}>Offer</option>
        <option value="salaryIncrement" onMouseOver={handleButtonClick}>Salary Increment</option>
        <option value="joining" onMouseOver={handleButtonClick}>Joining</option>
      </select>
    </div>
  );
};

export default Demo;
