import React, { useState } from 'react'
import './Attendance.css'
import Sidebar from './Sidebar'
import { useNavigate } from 'react-router-dom'
import axios from 'axios';
function Attendance() {
  const navigate = useNavigate();
  const [name, setname] = useState('');
  const [department, setdepartment] = useState('');
  const [designation, setdesignation] = useState('');
  const [employeeId, setEmployeeId] = useState('');
  const [lop, setlop] = useState('');
  const [leave, setleave] = useState('');
  const [Working, setworking] = useState('');
  const [totalworkingdays, settotalworkingdays] = useState('');
  const [holidays, setholidays] = useState('');
  const [sales, setsales] = useState('');
  const [extra, setextra] = useState('');
  const [shiftname, setshiftname] = useState('');
  const [shifttiming, setshifttiming] = useState('');
  const [month,setmonth] = useState('');

  const navigateToviewattendance = (e) => {
    e.preventDefault();
    navigate("/viewattendance");
  };

  //this block accept the data by id
  const acceptData = async (employeeId) => {
    try {
      const response = await axios.post('http://localhost:8000/api/acceptRecord', {
        employeeId: employeeId,
      });
      console.log(response);
      setname(response.data[0].name);
      setdesignation(response.data[0].designation);
      setdepartment(response.data[0].departmentId);
    } catch (error) {
      console.error(error);
    }
  };
  // console.log(accept);
  const handleAccept = (event) => {
    event.preventDefault();
    acceptData(employeeId);
  };
  // this api submit the details
  const sendData = async (employeeId,lop,leave,Working,totalworkingdays,holidays,sales,extra,shiftname,shifttiming,month,demp) => {
    try {
      const responseget = await axios.post('http://localhost:8000/api/attendance', {
        employeeId:employeeId,
        lop:lop,
        leave:leave,
        Working:Working,
        totalworkingdays:totalworkingdays,
        holidays:holidays,
        sales:sales,
        extra:extra,
        shiftname:shiftname,
        shifttiming:shifttiming,
        month:month,
        createdBy:demp,
      });
      if(responseget.data.success===true){
        alert(responseget.data.message)
      }
      else if(responseget.data.success===false){
        alert(responseget.data.message)
      }
    } catch (error) {
      console.error(error);
    }
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    const demp=localStorage.getItem('employeeId');// store the createdby id 
    console.log(demp);
    sendData(employeeId,lop,leave,Working,totalworkingdays,holidays,sales,extra,shiftname,shifttiming,month,demp);
  };

  return (
    <>
      <div id='main'>
        <Sidebar />
        <div id='attendance'>
        <h4>Attendance</h4>
        <div class="registration-form">
      <table className='table-attendance'>
        <thead>
          <h1>Information</h1>
        </thead>
        <tr>
          <td colspan="4"><input name="month" type="month"></input></td>
        </tr>
        <tr>
          <td className='table-data'><label for="firstName">First Name:</label></td>
          <td class="boxes"><input type="text" id="firstName" name="firstName"
              placeholder="Enter your first name" required></input></td>
          <td><label for="firstName">First Name:</label></td>
          <td class="boxes"><input type="text" id="firstName" name="firstName"
              placeholder="Enter your first name" required></input></td>
        </tr>
        <tr>
          <td  className='table-data'><label for="lastName">Last Name:</label></td>
          <td class="boxes"> <input type="text" id="lastName" name="lastName"
              placeholder="Enter your last name" required></input></td>
        </tr>
        <tr>
          <td  className='table-data'><label for="email">Email:</label></td>
          <td class="boxes"><input type="email" id="email" name="email"
              placeholder="Enter your email" required></input></td>
        </tr>
        <tr>
          <td  className='table-data'><label for="password">Password:</label></td>
          <td class="boxes"><input type="password" id="password" name="password"
              placeholder="Enter your password" required></input></td>
        </tr>
       
      </table>
      <button className='submit-button' type="submit">Register</button>
    </div>
          <h4>Attendance</h4>
          <form>
            <div className='information'>
              <div className='heading'>Information</div>
              <div className='month'><input type='month' name='month' onChange={(event) => setmonth(event.target.value)} ></input></div>
              <div className='details'><span>Employee ID : <input type='text' name='employee_id' onChange={(event) => setEmployeeId(event.target.value)}></input></span><button type='button' onClick={handleAccept}>Go</button><span>Employee Name : <input type='text' value={name} name='employee_name' disabled></input></span></div>
              <div className='details'><span>Designation : <input type='text' name='designation' disabled value={designation}></input></span><span>Shift Name : <select name='shiftname' onChange={(event) => setshiftname(event.target.value)}>
                <option value=""></option>
                <option value="Morning">Morning</option>
                <option value="Evening">Evening</option>
              </select>
              </span></div>
              <div className='details'><span>Department : <input type='text' name='department' disabled value={department}></input></span><span>Shift Timing : <select name='shiftname' onChange={(event) => setshifttiming(event.target.value)}>
                <option value=""></option>
                <option value="09:00 - 18:00">09:00 - 18:00</option>
                <option value="18:00 - 08:00">18:00 - 08:00</option>
              </select></span></div>
            </div>
            <div className='information'>
              <div className='heading'>Attendance Summary</div>
              <div className='details'>
                <span>LOP : <input type='text' name='lop' onChange={(event) => setlop(event.target.value)}></input></span>
                <span>Holidays : <input type='text' name='holiday' onChange={(event) => setholidays(event.target.value)}></input></span>
              </div>
              <div className='details'>
                <span>Leave Taken : <input type='text' name='leave_taken' onChange={(event) => setleave(event.target.value)}></input></span>
                <span>Extra Days : <input type='text' name='extra_days' onChange={(event) => setextra(event.target.value)}></input></span>
              </div>
              <div className='details'>
                <span>Working Days : </span><select name='working' onChange={(event) => setworking(event.target.value)}>
                  <option value=""></option>
                  <option value="31">January</option>
                  <option value="28">February</option>
                  <option value="31">March</option>
                  <option value="30">April</option>
                  <option value="31">May</option>
                  <option value="30">June</option>
                  <option value="31">July</option>
                  <option value="31">August</option>
                  <option value="30">September</option>
                  <option value="31">October</option>
                  <option value="30">November</option>
                  <option value="31">December</option>
                  </select>
                <span>Number of sales : <input type='text' name='sale_no' onChange={(event) => setsales(event.target.value)}></input></span>
              </div>
              <div className='details'>
                <span>Total Working Days : <input type='text' name='totalworking' onChange={(event) => settotalworkingdays(event.target.value)}></input></span>
              </div>
            </div>
            <div className='Button'>
              <button onClick={navigateToviewattendance}>View</button>
              <button type='reset'>Clear</button>
              <button type='submit' onClick={handleSubmit}>Submit</button>
            </div>
          </form>
        </div>
      </div>
    </>
  )
}

export default Attendance
