import React, { useEffect, useState } from 'react'
import './Attendance.css'
import Sidebar from './Sidebar'
import { useNavigate } from 'react-router-dom'
import axios from 'axios';
function Attendance() {
  const navigate = useNavigate();
  const [name, setname] = useState('');
  const [department, setdepartment] = useState('');
  const [designation, setdesignation] = useState('');
  const [employeeId, setEmployeeId] = useState('');
  const [lop,setlop]=useState('');
  const [leave,setleave]=useState('');
  const [Working,setworking]=useState('');
  const [totalworkingdays,settotalworkingdays]=useState('');
  const [holidays,setholidays]=useState('');
  const [sales,setsales]=useState('');
  const [extra,setextra]=useState('');
  const [shiftname,setshiftname]=useState('');
  const [shifttiming,setshofttiming]=useState('');

  const navigateToviewattendance = (e) => {
    e.preventDefault();
    navigate("/viewattendance");
  };
  
  //this block accept the data by id
  const acceptData=async(employeeId)=>{
     try{const response= await axios.post('http://localhost:8000/api/acceptRecord',{
        employeeId:employeeId,
      });
      console.log(response);
      setname(response.data[0].name);
      setdesignation(response.data[0].designation);
      setdepartment(response.data[0].departmentId);
    }catch(error){
      console.error(error);
    }
  };
  // console.log(accept);
  const handleAccept = (event) => {
    event.preventDefault();
    acceptData(employeeId);
  };
  // this api submit the details
  const sendData = async () => {
    try {
      const response = await axios.post('http://localhost:8000/api/', {
   
      });
    } catch (error) {
      console.error(error);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    sendData();
  };

  return (
    <>
    <div id='main'>
      <Sidebar />
      <div id='attendance'>
        <h4>Attendance</h4>
        <form>
          <div className='information'>
            <div className='heading'>Information</div>
            <div className='month'><input type='month' name='month' ></input></div>
            <div className='details'><span>Employee ID : <input type='text' name='employee_id' onChange={(event) => setEmployeeId(event.target.value)}></input></span><button type='button' onClick={handleAccept}>Go</button><span>Employee Name : <input type='text' value={name} name='employee_name'></input></span></div>
            <div className='details'><span>Designation : <input type='text'  name='designation' value={designation}></input></span><span>Shift Name : <select name='shiftname' onChange={(event) => setEmployeeId(event.target.value)}>
              <option value=""></option>
              <option value="Morning">Morning</option>
              <option value="Evening">Evening</option>
            </select>
              </span></div>
            <div className='details'><span>Department : <input type='text'  name='department' value={department}></input></span><span>Shift Timing : <select name='shiftname' onChange={(event) => setEmployeeId(event.target.value)}>
              <option value=""></option>
              <option value="09:00 - 18:00">09:00 - 18:00</option>
              <option value="18:00 - 08:00">18:00 - 08:00</option>
            </select></span></div>
          </div>
          <div className='information'>
            <div className='heading'>Attendance Summary</div>
            <div className='details'><span>LOP : <input type='text' name='lop'></input></span><span>Holidays : <input type='text' name='holiday'></input></span></div>
            <div className='details'><span>Leave Taken : <input type='text' name='leave_taken'></input></span><span>Extra Days : <input type='text' name='extra_days'></input></span></div>
            <div className='details'><span>Working Days : <input type='text' name='working_days'></input></span><span>Number of sales : <input type='text' name='sale_no'></input></span></div>
            <div className='details'><span>Total Working Days : <input type='number' name='tworking_days'></input></span></div>
          </div>
          <div className='Button'>
            <button onClick={navigateToviewattendance}>View</button>
            <button type='reset'>Clear</button>
            <button type='submit'>Submit</button>
          </div>
        </form>
      </div>
    </div>
    </>
  )
}

export default Attendance
