import React, {useState} from 'react'
import Sidebar from './Sidebar'
import Buttons from './Buttons'
import './Addbasicdetails.css'
import {useNavigate} from 'react-router-dom'

function Addbasicdetailsadmin() {
    const navigate = useNavigate();
    const [password, setPassword] = useState('');

    
  const generatePassword = (event) => {
    event.preventDefault();
    const length = 20; // length of the generated password
    const characters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*()_+~`|}{[]\:;?><,./-=';
    let newPassword = '';

    for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      newPassword += characters[randomIndex];
    }

    setPassword(newPassword);
  };

    const navigatetoAddadmin = () => {
        navigate("/AddAdmin");
      };

  return (
    <div id='main'>
      <Sidebar />
      <div className='basicdetail'>
        <div className='basicdetail-container'>
            <h4>User Registration</h4>
            <form name='' action='' method='post'>
                <div className='basicinput'>
                    <div>Name : <input type='text' name='name' /></div>
                    <div>Email : <input type='email' name='email'/></div>
                    <div className='password'><input type='text' name='password' value={password}/></div>
                    <div><button className='Basicbutton' onClick={navigatetoAddadmin}>Submit</button></div>
                </div>
            </form>
        </div>
      </div>
      <Buttons />
    </div>
  )
}

export default Addbasicdetailsadmin
