import React, { useState } from 'react'
import './Search.css'
import Sidebar from './Sidebar'
import Buttons from './Buttons'
// import axios from 'axios';
import FetchAPI from './FetchAPI'

function Searchadmin({ name }) {
  const [id, setid] = useState(0);
  const [data,setdata] =useState([]);


  //     const [data] = FetchAPI(`http://localhost:8000/api/show-all-admin/${id}`);

  const handleSubmit =()=>{
    try {
      // const [data] = FetchAPI(`http://localhost:8000/api/search-admin/${id}`);
      setdata(FetchAPI(`http://localhost:8000/api/search-admin/${id}`));
      console.log(data)
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div id='main'>
      <Sidebar />
      <div id='search'>
        <h4>{name}</h4>
        <div className='searcharea'>
          <input type='search'  onChange={(event) => setid(event.target.value)} placeholder='Enter Admin Id' name='id'></input>
          <button type='submit' name='search' onClick={handleSubmit}>Search</button>
        </div>
        <div className='searchoption'>
          <span>Profile</span>
          <span>ID</span>
          <span>Name</span>
          <span>Office Email</span>
          <span>Contact Number</span>
          <span>Postion</span>
          <span>Document</span>
          <span>Edit</span>
          <span>Delete</span>
        </div>
        {/* {
          data.map((data, index) => {
            return(
              <div key={index} className='searchoption'>
                <span>{index + 1}</span>
                <span>{data.name}</span>
                <span>{data.employee_id}</span>
                <span>{data.office_email}</span>
                <span>{data.contact_no}</span>
                <span>{data.designation}</span>
                <select>
                  <option value="select letter"><button>select letters</button></option>
                  <option value="Relieving Letter"><button>Relieving</button></option>
                  <option value="Experience Letter"><button>Experience</button></option>
                  <option value="Offer Letter"><button>Offer</button></option>
                  <option value="Salary Increment Letter"><button>Salary Increment</button></option>
                  <option value="Joining Letter"><button>Joining</button></option>
                </select>
                <button>Edit</button>
                <button>Delete</button>
              </div>
              )
            })
        } */}
      </div>
      <Buttons />
    </div>
  )
}

export default Searchadmin
