    setfield(deletefield)

  }
  return (
    <div id='main'>
      <Sidebar />
      <div>
      {/* Display existing input fields */}
      {inputFields.map((field, index) => (
        <input
          key={index}
          value={field}
          onChange={(e) => {
            const values = [...inputFields];
            values[index] = e.target.value;
            setInputFields(values);
          }}
        />
